$(function  () {
    layui.use('form', function(){
        var form = layui.form;
        var subName =  $("input:submit").attr('lay-filter');
        //监听提交
        form.on('submit('+subName+')', function(data){
            var index = layer.load(1);
            $.post("",data.field,function(res){
                layer.close(index);
                layer.msg(JSON.stringify(res.info),{time:1600},function(){
                    location.href= res.url;
                });
            });
            return false;
        });
    });
})